mapper.select:
select user_id, product_id, buy_count, specification_id
from shopping_cart
where user_id = #{user_id}
  and product_id = #{product_id}
  and specification_id = #{specification_id}

mapper.delete:
delete
from shopping_cart
where user_id = #{user_id}
  and product_id = #{product_id}

mapper.update:
update shopping_cart
set buy_count = #{buy_count}
where user_id = #{user_id}
  and product_id = #{product_id}
  and specification_id = #{specification_id}

mapper.delete:
delete
from shopping_cart
where user_id = #{user_id}
  and product_id = #{product_id}
  and specification_id = #{specification_id}
